var QuickNav=function(){
		return{
			init:function(){
				if($(".quick-nav").length>0){
					var i=$(".quick-nav");
					i.each(function(){
						var i=$(this),
						n=i.find(".quick-nav-trigger");
						n.on("click",function(n){
							n.preventDefault(),
							i.toggleClass("nav-is-visible");
							
							var $el = $( '#baraja-el' ),
							baraja = $el.baraja();							
							baraja.fan( {
								speed : 500,
								easing : 'ease-out',
								range : 15,
								direction : 'right',
								origin : { x : 50, y : 200 },
								center : false,
								scatter : false,
								translation : 300
							} );
						})
					}),
					$(document).on("click",function(n){
						!$(n.target).is(".quick-nav-trigger")&&
						!$(n.target).is(".quick-nav-trigger span")&&
						!$(n.target).is(".baraja-wizard")&&
						i.removeClass("nav-is-visible");					
					})
				}
			}
		}
	}();
	QuickNav.init();